    {
      "_id": "64f8a1b2c3d4e5f6a7b8c9e1",
      "value": 0,
      "description": "Sıfır rakamı",
      "examples": [
        {
          "imageUrl": "https://opca-bucket.s3.amazonaws.com/digits/examples/digit_0_example1.jpg",
          "description": "El yazısı 0 örneği"
        }
      ]
    },
    {
      "_id": "64f8a1b2c3d4e5f6a7b8c9e2",
      "value": 1,
      "description": "Bir rakamı",
