    },
    "processingDistribution": {
      "mobile": 800,
      "server": 450
    },
    "dailyAnalyses": [
      {
        "_id": {
          "year": 2023,
          "month": 9,
          "day": 15
        },
        "count": 25,
        "parasiteCount": 15,
        "mnistCount": 10
      }
    ],
    "topUsers": [
      {
        "_id": "64f8a1b2c3d4e5f6a7b8c9d0",
        "analysisCount": 45,
        "userName": "Test Kullanıcı",
